-- Warehouse Management App Database Schema\n\nCREATE TABLE Warehouses (\n    WarehouseID INT PRIMARY KEY IDENTITY(1,1),\n    Name NVARCHAR(100) NOT NULL,\n    Location NVARCHAR(255) NOT NULL,\n    Capacity INT NOT NULL,\n    CreatedAt DATETIME DEFAULT GETDATE()\n);\n\nCREATE TABLE WarehouseServiceProviders (\n    ServiceProviderID INT PRIMARY KEY IDENTITY(1,1),\n    Name NVARCHAR(100) NOT NULL,\n    ContactInfo NVARCHAR(255),\n    CreatedAt DATETIME DEFAULT GETDATE()\n);\n\nCREATE TABLE WarehouseWSPMapping (\n    MappingID INT PRIMARY KEY IDENTITY(1,1),\n    WarehouseID INT REFERENCES Warehouses(WarehouseID),\n    ServiceProviderID INT REFERENCES WarehouseServiceProviders(ServiceProviderID),\n    CreatedAt DATETIME DEFAULT GETDATE()\n);\n\nCREATE TABLE WarehouseSupervisors (\n    SupervisorID INT PRIMARY KEY IDENTITY(1,1),\n    Name NVARCHAR(100) NOT NULL,\n    WarehouseID INT REFERENCES Warehouses(WarehouseID),\n    CreatedAt DATETIME DEFAULT GETDATE()\n);\n\nCREATE TABLE WarehouseAgreements (\n    AgreementID INT PRIMARY KEY IDENTITY(1,1),\n    WarehouseID INT REFERENCES Warehouses(WarehouseID),\n    ServiceProviderID INT REFERENCES WarehouseServiceProviders(ServiceProviderID),\n    StartDate DATETIME NOT NULL,\n    EndDate DATETIME NOT NULL,\n    CreatedAt DATETIME DEFAULT GETDATE()\n);\n\nCREATE TABLE WarehouseCompliance (\n    ComplianceID INT PRIMARY KEY IDENTITY(1,1),\n    WarehouseID INT REFERENCES Warehouses(WarehouseID),\n    ComplianceDate DATETIME NOT NULL,\n    Status NVARCHAR(50) NOT NULL,\n    CreatedAt DATETIME DEFAULT GETDATE()\n);\n\n-- Creating indexes for optimization\nCREATE INDEX IDX_Warehouse_Name ON Warehouses (Name);\nCREATE INDEX IDX_ServiceProvider_Name ON WarehouseServiceProviders (Name);\nCREATE INDEX IDX_WSPMapping_WarehouseID ON WarehouseWSPMapping (WarehouseID);\nCREATE INDEX IDX_Supervisor_WarehouseID ON WarehouseSupervisors (WarehouseID);\nCREATE INDEX IDX_Agreement_WarehouseID ON WarehouseAgreements (WarehouseID);\nCREATE INDEX IDX_Compliance_WarehouseID ON WarehouseCompliance (WarehouseID);\n